  useEffect(() => {
    fetch("http://localhost:3001/toys")
    .then(r => r.json())
    .then(toyData => {

    })
  },[])

  function handleClick() {
    setShowForm((showForm) => !showForm);
  }

  return (
    <>
      <Header />
      {showForm ? <ToyForm /> : null}
      <div className="buttonContainer">
        <button onClick={handleClick}>Add a Toy</button>
      </div>
      <ToyContainer />
    </>
  );
}

